@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Web Storage API';
}

<h1 style="text-align: center; color: blueviolet;">
  {{title}}
</h1>


<app-lstorage></app-lstorage>

